function valid(){var username,mail,password,repeat,expresion;username=document.getElementById('username').value;email=document.getElementById('mail').value;password=document.getElementById('password').value;repeat=document.getElementById('repeat').value;expresion=/\w+@\w+\.+[a-z]/;if(username===""||email===""||password===""||repeat===""){alert("All fields are required");navigator.vibrate(500);return!1}else if(email.length>100){alert("The email is very length");navigator.vibrate(500);return!1}else if(!expresion.test(email)){alert("The email is not valid");navigator.vibrate(500);return!1}else if(username.length>20||password.length>20){alert("The username and password should only have 20 characters maximum");navigator.vibrate(500);return!1}else if(password.length<8){alert("The password is very short");navigator.vibrate(500);return!1}else if(password!=repeat){alert("Passwords do not match");navigator.vibrate(500);return!1}}